mapper.insert:
INSERT INTO board (

) VALUES (
	  #{idx}
	, #{title}
	, #{content}
	, #{writer}
	, 0
	, IFNULL(#{noticeYn}, 'N')
	, IFNULL(#{secretYn}, 'N')
	, 'N'
	, NOW()
	, NULL
	, NULL
	, #{email}
	, #{grade}
	, #{gradeup}
	, #{skills}
	, #{git}
	, #{birthday}
	, #{username}
)

mapper.update:
UPDATE board
SET
	  update_time = NOW()
	, title = #{title}
	, content = #{content}
	, writer = #{writer}
	, notice_yn = IFNULL(#{noticeYn}, 'N')
	, secret_yn = IFNULL(#{secretYn}, 'N')
	, email = #{email}
	, grade = #{grade}
	, gradeup = #{gradeup}
	, skills = #{skills}
	, git = #{git}
	, birthday = #{birthday}
WHERE
	idx = #{idx}
